mapper.select:
SELECT t1.*, t2.* FROM( SELECT b.pcode , b.email , func_code('REMAINSTOCKYN', b.remainStockYn) AS remainStockYn , func_code('EMPTYSTOCKYN', b.emptyStockYn) AS emptyStockYn , b.rnum as num FROM( SELECT ROWNUM AS rnum, a.* FROM ( SELECT * FROM notice WHERE 1 = 1 --검색조건
)A WHERE ROWNUM <= (#{pageSize} * (#{pageNum} -1) + #{pageSize}) )B WHERE rnum >= (#{pageSize} * (#{pageNum} -1) +1) )t1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM notice WHERE 1 = 1 --검색조건
)t2

SELECT
	
FROM notice
WHERE pcode = #{pcode}
AND email = #{email}

mapper.insert:
INSERT INTO notice (
	pcode	
	, email		
	, remainStockYn	
	, emptyStockYn	
) VALUES (
	#{pcode, jdbcType = VARCHAR}
	, #{email, jdbcType = VARCHAR}	
	, #{remainStockYn, jdbcType = VARCHAR}	
	, #{emptyStockYn, jdbcType = VARCHAR}
)
