mapper.select:
SELECT t1.*, t2.* FROM( SELECT b.rno , b.pcode , m.name AS name , CryptString.decrypt(m.ihidnum,'key') AS ihidnum , p.name AS pname , p.addr AS paddr , b.maskCnt , func_code('APPROVAL', b.approval) AS approval , b.impuid , b.amount , b.regId , TO_CHAR(b.regDt, 'YYYY/MM/DD') AS regDt , b.modId , TO_CHAR(b.modDt, 'YYYY/MM/DD') AS modDt , b.rnum as num FROM( SELECT ROWNUM AS rnum, a.* FROM ( SELECT * FROM reservation WHERE 1 = 1 --검색조건
)A WHERE ROWNUM <= (#{pageSize} * (#{pageNum} -1) + #{pageSize}) )B, member m, pharmacy P WHERE b.pcode = p.pcode AND b.regId = m.email AND rnum >= (#{pageSize} * (#{pageNum} -1) +1) )t1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM reservation WHERE 1 = 1 --검색조건
)t2

mapper.update:
UPDATE reservation
SET approval = #{approval, jdbcType = VARCHAR}  
WHERE rno = #{rNo, jdbcType = VARCHAR}
